import * as React from "react";

interface props {
  itemName?: string;
  //onchange: (event: React.ChangeEvent<HTMLInputElement>) => void;
  //submitClick: React.MouseEventHandler<HTMLButtonElement>;
}
export default function InputTask(prop: props) {
  function closeInput() {
    let inputTask = document.getElementById("input-task");
    if (inputTask !== null) inputTask.style.display = "none";
  }
  return (
    <div id="input-task" style={{ display: "none", marginTop:"5%" }}>
      <div>
      <input value={prop.itemName}  />
      </div>
      <button onClick={closeInput}> Cancel </button>
      <button> Submit </button>
    </div>
  );
}
