import React, { useState } from "react";
import Button from "@material-ui/core/Button";
import Fade from "@material-ui/core/Fade";
import Box from "@material-ui/core/Box";

interface AddTaskProps {
    submitOnClick: React.MouseEventHandler<HTMLButtonElement>;
    onkeyup: (event:React.KeyboardEvent<HTMLInputElement>)=>void;
}

export default function AddTask(prop:AddTaskProps) {
  const [checked, setChecked] = useState(false);
  const [content, setContent] = useState("");
  function showInput() {
    setContent("");
    setChecked(true);
  }
  function hideInput() {
    setChecked(false);
  }

  return (
    <Box sx={{ marginTop: "5%", textAlign: "center", }}>
      <Button onClick={showInput} variant="contained">
        New Task
      </Button>
      <div
        style={{ display: "flex", justifyContent: "center", marginTop: "2%" }}
      >
        <Fade in={checked} timeout={1000}>
          <div
            style={{
              backgroundColor: "#e5eff5",
              width: "30%",
              height: "150px",
              borderRadius: "8px",
              textAlign: "center",
            }}
          >
            <input
              id="add-content"
              style={{ marginTop: "10%", height: "30px", width: "60%", fontSize:"16px"}}
              value={content}
              placeholder="Add Content Here"
              onChange={(event) => {
                  setContent(event.target.value)
              }}
              onKeyUp = {onkeyup}
            />
            <div
              style={{
                marginTop: "5%",
                display: "flex",
                width: "60%",
                marginLeft: "20%",
                justifyContent: "space-between",
              }}
            >
              <Button onClick={prop.submitOnClick}> Submit </Button>
              <Button onClick={hideInput}> Cancel </Button>
            </div>
          </div>
        </Fade>
      </div>
    </Box>
  );
}
