import React, {useState} from "react";
import Button from "@material-ui/core/Button";
import Fade from "@material-ui/core/Fade";
import Box from "@material-ui/core/Box";

export default function AddTask() {
    const[checked, setChecked] = useState(false);
    function showInput(){
        setChecked(true);
    }
    function hideInput(){
        setChecked(false);
    }
  return (
    <Box sx={{ marginTop: "5%", textAlign:"center", border:"2px solid red"}}>
      <Button onClick={showInput} variant="contained"> New Task </Button>
      <div style={{display:"flex", justifyContent:"center"}}>
      <Fade in={checked} timeout={1000}>
        <div style={{marginTop:"5%", backgroundColor:"#e5eff5", width:"30%", height:"150px", borderRadius:"8px", textAlign:"center"}}>
              <input style={{marginTop:"10%", height:"30px", width:"60%"}} value="" placeholder="Add Content here"/>
              <br/>
              <div style={{marginTop:"5%", display:"flex",width:"60%", marginLeft:"20%", justifyContent:"space-between",}}>
              <Button> Submit </Button>
              <Button onClick={hideInput}> Cancel </Button>
              </div>
        </div>
      </Fade>
      </div>
    </Box>
  );
}
