import * as React from "react";
import { Draggable, Droppable } from "react-beautiful-dnd";
import styled from "styled-components";
import List from "@material-ui/core/List";

// Import BoardItem component
import { BoardItem } from "./board-item";

// Define types for board column element properties
type BoardColumnProps = {
  index: number;
  key: string;
  column: any;
  items: any;
  droppable: boolean;
  onkey: (event: React.KeyboardEvent<HTMLInputElement>) => void;

};

// Define types for board column content style properties
// This is necessary for TypeScript to accept the 'isDraggingOver' prop.
type BoardColumnContentStylesProps = {
  isDraggingOver?: boolean;
  isDragging?: boolean;
};

// Create styles for BoardColumnWrapper element
const BoardColumnWrapper = styled.div<BoardColumnContentStylesProps>`
  flex: 1;
  padding: 8px;
  background-color: #e5eff5;
  border-radius: 4px;
  background-color: ${(props) => (props.isDragging ? "#545454" : "#e5eff5")};

  & + & {
    margin-left: 12px;
  }
`;

// Create styles for BoardColumnTitle element
const BoardColumnTitle = styled.h2`
  font: 16px sans-serif;
  margin-bottom: 12px;
`;

// Create styles for BoardColumnContent element
const BoardColumnContent = styled.div<BoardColumnContentStylesProps>`
  min-height: 20px;
  background-color: ${(props) => (props.isDraggingOver ? "#aecde0" : null)};
  border-radius: 4px;
`;

// Create and export the BoardColumn component
export const BoardColumn: React.FC<BoardColumnProps> = (props) => {
  return (
    <Draggable draggableId={props.column.id} index={props.index}>
      {(provided1, snapshot1) => (
        <BoardColumnWrapper
          {...provided1.draggableProps}
          {...provided1.dragHandleProps}
          ref={provided1.innerRef}
          isDragging={snapshot1.isDragging}
        >
          <BoardColumnTitle>{props.column.title}</BoardColumnTitle>
          <List style={{maxHeight: '20%', overflow: 'auto'}} >
          <Droppable droppableId={props.column.id} direction="vertical" isDropDisabled={props.droppable}>
            {(provided, snapshot) => (
              <BoardColumnContent
                {...provided.droppableProps}
                ref={provided.innerRef}
                isDraggingOver={snapshot.isDraggingOver}
              >
                {props.items.map((item: any, index: number) => (
                  <BoardItem
                    key={item.id}
                    item={item}
                    index={index}
                    onkey={props.onkey}
                  />
                ))}
                {provided.placeholder}
              </BoardColumnContent>
            )}
          </Droppable>
          </List>
        </BoardColumnWrapper>
      )}
    </Draggable>
  );
};
