import React, {useState} from "react";
import { Draggable } from "react-beautiful-dnd";
import styled from "styled-components";

// Define types for board item element properties
type BoardItemProps = {
  index: number;
  item: any;
  onkey: (event:React.KeyboardEvent<HTMLInputElement>)=>void;
  onmouse: (event:React.MouseEvent<HTMLInputElement>) => void;
};

// Define types for board item element style properties
// This is necessary for TypeScript to accept the 'isDragging' prop.
type BoardItemStylesProps = {
  isDragging: boolean;
};

// Create style for board item element
const BoardItemEl = styled.div<BoardItemStylesProps>`
  padding: 8px;
  background-color: ${(props) => (props.isDragging ? "#545454" : "#fff")};
  border-radius: 4px;
  transition: background-color 0.25s ease-out;

  &:hover {
    background-color: #f8fafc;
  }

  & + & {
    margin-top: 4px;
  }
`;

// Create and export the BoardItem component
export const BoardItem = (props: BoardItemProps) => {
  const[content, setContent] = useState(props.item.content);
  return (
    <Draggable draggableId={props.item.id} index={props.index}>
      {(provided, snapshot) => (
        <BoardItemEl
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          ref={provided.innerRef}
          isDragging={snapshot.isDragging}
        >
          <input
            id = {props.item.id}
            style={{ border: "none", backgroundColor: "transparent" }}
            value={content}
            onChange={(event) => {
              setContent(event.target.value);
            }}
            onKeyUp={props.onkey}
            onMouseOut={props.onmouse}
          />
        </BoardItemEl>
      )}
    </Draggable>
  );
};
