import * as React from "react";
import { DragDropContext } from "react-beautiful-dnd";
import styled from "styled-components";
import InputTask from "./InputTask";

// Import data for board
import { initialBoardData } from "../data/board-initial-data";

// Import BoardColumn component
import { BoardColumn } from "./board-column";

// Create styles board element properties
const BoardEl = styled.div`
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
`;
const AddTaskButton = styled.button`
  border-radius: 8px;
  margin-top: 10%;
`;

export class Board extends React.Component {
  // Initialize board state with board data
  state = { data: initialBoardData, itemContent: "" };

  editContent = (event:React.KeyboardEvent<HTMLInputElement>) =>{
    if (event.key === "Enter") {
      //
      console.log((event.target as HTMLInputElement).value);
      console.log((event.target as HTMLInputElement).id);
      (event.target as HTMLInputElement).blur();}
  };

  showInputTask = () => {
    this.setState({ itemContent: "" });
    let inputTask = document.getElementById("input-task");
    if (inputTask !== null) inputTask.style.display = "block";
  };

  addNewTask = () => {
    if (this.state.itemContent === "") return;
    // auto generate item_id
    let item_index = Object.keys(this.state.data.items).length + 1;
    let item_id: string = "item-" + item_index;

    // default to put the new added items into column4
    const columnAdd = (this.state.data.columns as any)["column-4"];

    const newItemsIds = Array.from(columnAdd.itemsIds);

    newItemsIds.push(item_id);
    console.log(newItemsIds);
    const newColumnAdd = {
      ...columnAdd,
      itemsIds: newItemsIds,
    };
    const newState = {
      ...this.state.data,
      items: {
        ...this.state.data.items,
        [item_id]: { id: item_id, content: this.state.itemContent },
      },
      columns: {
        ...this.state.data.columns,
        "column-4": newColumnAdd,
      },
    };
    this.setState({ data: newState });
    console.log(this.state.data);
  };

  // Handle drag & drop
  onDragEnd = (result: any) => {
    const { source, destination, draggableId } = result;

    // Do nothing if item is dropped outside the list
    if (!destination) {
      return;
    }

    // Do nothing if the item is dropped into the same place
    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) {
      return;
    }

    // Find column from which the item was dragged from
    const columnStart = (this.state.data.columns as any)[source.droppableId];

    // Find column in which the item was dropped
    const columnFinish = (this.state.data.columns as any)[
      destination.droppableId
    ];

    // Moving items in the same list
    if (columnStart === columnFinish) {
      // Get all item ids in currently active list
      const newItemsIds = Array.from(columnStart.itemsIds);

      // Remove the id of dragged item from its original position
      newItemsIds.splice(source.index, 1);

      // Insert the id of dragged item to the new position
      newItemsIds.splice(destination.index, 0, draggableId);

      // Create new, updated, object with data for columns
      const newColumnStart = {
        ...columnStart,
        itemsIds: newItemsIds,
      };

      // Create new board state with updated data for columns
      const newState = {
        ...this.state.data,
        columns: {
          ...this.state.data.columns,
          [newColumnStart.id]: newColumnStart,
        },
      };

      // Update the board state with new data
      this.setState({ data: newState });
    } else {
      // Moving items from one list to another
      // Get all item ids in source list
      const newStartItemsIds = Array.from(columnStart.itemsIds);

      // Remove the id of dragged item from its original position
      newStartItemsIds.splice(source.index, 1);

      // Create new, updated, object with data for source column
      const newColumnStart = {
        ...columnStart,
        itemsIds: newStartItemsIds,
      };

      // Get all item ids in destination list
      const newFinishItemsIds = Array.from(columnFinish.itemsIds);

      // Insert the id of dragged item to the new position in destination list
      newFinishItemsIds.splice(destination.index, 0, draggableId);

      // Create new, updated, object with data for destination column
      const newColumnFinish = {
        ...columnFinish,
        itemsIds: newFinishItemsIds,
      };

      // Create new board state with updated data for both, source and destination columns
      const newState = {
        ...this.state.data,
        columns: {
          ...this.state.data.columns,
          [newColumnStart.id]: newColumnStart,
          [newColumnFinish.id]: newColumnFinish,
        },
      };

      // Update the board state with new data
      this.setState({ data: newState });
    }
  };

  render() {
    return (
      <div style={{ textAlign: "center" }}>
        <BoardEl>
          <DragDropContext onDragEnd={this.onDragEnd}>
            {this.state.data.columnsOrder.map((columnId) => {
              // Get id of the current column
              const column = (this.state.data.columns as any)[columnId];

              // Get item belonging to the current column
              const items = column.itemsIds.map(
                (itemId: string) => (this.state.data.items as any)[itemId]
              );

              // Render the BoardColumn component
              return (
                <BoardColumn
                  key={column.id}
                  column={column}
                  items={items}
                  onkey={this.editContent}
                />
              );
            })}
          </DragDropContext>
        </BoardEl>
        <AddTaskButton onClick={this.showInputTask}> New Task </AddTaskButton>
        <InputTask
          submitClick={this.addNewTask}
          itemName={this.state.itemContent}
          onchange={(event) => {
            this.setState({ itemContent: event.target.value });
          }}
        />
      </div>
    );
  }
}
